/**
 * @class           {Object} Cursor                             Cursor object
 * @property        {Point}  position                           X & Y axis coordinates
 * @property        {string} id                                 Cursor's DOM identifier
 * @property        {string} type                               Type of cursor
 * @property        {Object} config                             Internal private configuration
 * @property        {Object} tools                              Internal private utility methods
 */
class Cursor
{
    _id       = 'ui-cursor';
    _type     = 'pointer';
    _position = new Point;

    #config =
    {
        calculations:
        {
            distance: undefined,
            angle:    undefined
        },
        cache:
        {
            avoid:    [ 'html', 'ui-cursor', 'canvas' ],
            over:     [ ],
            position: undefined
        },
        presentation:
        {
            settings:
            {
                os: 'mac',              // Mac, Win, Linux
                css:
                {
                    cursors:
                    {
                        'position':     'absolute',
                        'left':          window.innerWidth  / 2,
                        'top':           window.innerHeight / 2,
                        'width':         25,
                        'z-index':       9999,
                        offset:
                        {
                            left: -7,
                            top:  -2
                        }
                    },
                    ui:
                    {
                        select:
                        {
                            ul:
                            {
                                'position':         'absolute',
                                'width':            '25%',
                                'padding':          '5px 6px',
                                'z-index':           9998,
                                'background':       'rgb(106,104,105)',
                                'border':           '1px solid rgb(33,33,33)',
                                'border-radius':     5,
                                'color':            'white',
                                'list-style':       'none',
                                'box-shadow':       'inset 0px 0px 0px 1px rgb(137,133,134)',
                            },
                            li:
                            {
                                'border-radius':     3,
                                'padding-bottom':    2
                            },
                            icon:
                            {
                                'width':             20,
                                'display':          'inline-block'
                            }
                        }
                    }
                }
            },
            mac:
            {
                cursors:
                {
                    allScroll:              'data:image/png;base64,',
                    click:                  'data:image/png;base64,',
                    copy:                   'data:image/png;base64,',
                    cross:                  'data:image/png;base64,',
                    handGrab:               'data:image/png;base64,',
                    handOpen:               'data:image/png;base64,',
                    handPoint:              'data:image/png;base64,',
                    pencil:                 'data:image/png;base64,',
                    plus:                   'data:image/png;base64,',
                    pointer:                'data:image/png;base64,',
                    question:               'data:image/png;base64,',
                    resizeDiagonalLeft:     'data:image/png;base64,',
                    resizeDiagonalRight:    'data:image/png;base64,',
                    resizeHorizontal:       'data:image/png;base64,',
                    resizeVertical:         'data:image/png;base64,',
                    text:                   'data:image/png;base64,',
                    unavailable:            'data:image/png;base64,',
                    zoomIn:                 'data:image/png;base64,',
                    zoomOut:                'data:image/png;base64,'
                },
                ui:
                {
                    checkmark:              'data:image/svg+xml;base64,'
                }
            },
            win:
            {
                // @TODO: implement common windows cursors
            },
            linux:
            {
                // @TODO: implement common linux cursors
            }
        }
    }

    #tools =
    {
        ////    CONVERSION    //////////////////////////////////

            /**
             * Converts CSS string value to number/integer
             * @param           {string} value                              CSS string value in pixels
             * @return          {number}                                    Number value of parsed value
             */
            pxToNumber: ( value ) => Number ( value.replace ( /px$/, '' ) ),

            /**
             * Converts the 'css' object into a CSS inline string
             * @param           {object} json                               CSS object from #config.presentation.settings.css [ type ]
             * @return          {string}                                    CSS inline string
             */
            jsonToCss:  ( json )  =>
            {
                let _result = '';


                for ( let [ _key, _value ] of Object.entries ( json ) )
                {
                    _value   = ( typeof _value === 'number' && _key != 'z-index') ? `${_value}px` : _value;


                    _result += `${_key}: ${_value}; `
                }


                return _result;
            },

        ////    SETTERS    /////////////////////////////////////

            /**
             * Set's the cursor type based on passed element's tag name
             * @param           {Object} element                            HTML DOM element
             */
            setCursorType: ( element )  =>
            {
                let elements = [ 'A', 'INPUT', 'SELECT', 'BUTTON' ];


                if ( elements.includes ( element.tagName ) )

                    this.switchType ( 'handPoint' );
            },

        ////    GETTERS    /////////////////////////////////////

            /**
             * Gets the center point of an element
             * @param           {Object} element                            HTML DOM element
             * @return          {Point}                                     X & Y Coordinates
             */
            getCenterPoint: ( element ) =>
            {
                ////    FUNCTIONS    ///////////////////////

                let _getCenter = ( ) =>
                ( {
                      x: element.getBoundingClientRect ( ).left + ( element.getBoundingClientRect ( ).width  / 2 ) + this.#config.presentation.settings.css.cursors.offset.left,

                      y: element.getBoundingClientRect ( ).top  + ( element.getBoundingClientRect ( ).height / 2 ) + this.#config.presentation.settings.css.cursors.offset.top + window.scrollY
                } );

                ////    LOGIC    ///////////////////////////

                let _center = _getCenter ( );


                if ( element.type === 'range' )
                {
                    let _rect     = element.getBoundingClientRect ( );

                        _center.x = Number ( _rect.left + ( ( element.value / element.max ) * _rect.width ) );
                }


                return _center;
            },

            /**
             * Gets the center point of the value of a range element
             * @param           {Object} element                            HTML Range element
             * @return          {Point}                                     X & Y Coordinates
             */
            // getRangeValuesPoint: ( element ) =>
            // {
            //     let _point   = this.#tools.getCenterPoint ( element );

            //     let _rect    = element.getBoundingClientRect ( );

            //     ////////////////////////////////////////////

            //         _point.x = Number ( _rect.left + ( ( element.value / element.max ) * _rect.width ) );


            //     return _point;
            // },

        ////    MOUSE EVENTS    ////////////////////////////////

            /**
             * Actions executed after a mouseover event
             * @param           {HTMLElement} element                       HTML DOM element
             */
            mouseover: ( element ) =>
            {
                if ( element.getAttribute ( `onmouseover` ) != null )
                {
                    element.onmouseover ( );


                    this.#config.cache.over.push ( element );
                }
            },

            /**
             * Actions executed after a mouseout event
             * @param           {HTMLElement} element                       HTML DOM element
             */
            mouseout:  ( element ) =>
            {
                if ( element.getAttribute ( `onmouseout` ) != null )
                {
                    element.onmouseout ( );


                    this.#config.cache.over.shift ( );
                }
            },

        ////    CREATION    ////////////////////////////////////

            /**
             * Create and embeds cursor within DOM
             * @param           {string} id                                 Identifier of cursor
             * @param           {string} type                               Cursor type within #config.presentation [ os ].cursors [ type ]
             */
            createCursor: ( id, type ) =>
            {
                let _settings = this.#config.presentation.settings;

                    _settings.css.cursors.top  += _settings.css.cursors.offset.top;

                    _settings.css.cursors.left += _settings.css.cursors.offset.left;

                ////////////////////////////////////////////////////

                let _image    = document.createElement ( 'img' );

                    _image.id            = id;

                    _image.src           = this.#config.presentation [ _settings.os ].cursors [ type ];

                    _image.style.cssText = this.#tools.jsonToCss ( _settings.css.cursors )


                    _image.onerror       = ( ) => console.log ( ' >> [ ERROR ]: Image could not be loaded !'   );

                    _image.onload        = ( ) => console.log ( ' >> [ SUCCESS ]: Image loaded successfully !' );

                ////////////////////////////////////////////////////

                document.body.insertBefore ( _image, document.body.firstChild );


                this.position =
                {
                    x: this.#tools.pxToNumber ( _image.style.left ),

                    y: this.#tools.pxToNumber ( _image.style.top  )
                }
            }
    }

    /**
     * Create a single instance of a Cursor
     * @param           {string} position                           X & Y axis coordinates
     * @param           {string} id                                 Cursor's DOM identifier
     * @param           {string} type                               Type of cursor, within #_cursors
     */
    constructor ( position = new Point, id, type )
    {
        this.position = position;

        this.id       = id;

        this.type     = ( type === undefined ) ? this._type : type;


        this.#tools.createCursor ( this._id, this._type );
    }

    ////    [ ID ]    //////////////////////////////////////

        /**
         * Set id property
         * @param           {string} id                                 Identifier of Cursor
         */
        set id ( id )
        {
            this._id = ( document.getElementById ( id ) != null ) ? id : this._id;
        }

        /**
         * Get id property
         * @return          {string}                                    Identifier of Cursor
         */
        get id ( )
        {
            return this._id;
        }

    ////    [ TYPE ]    ////////////////////////////////////

        /**
         * Set type property
         * @param           {string} value                              Type property
         */
        set type ( value )
        {
            let _cursors = this.#config.presentation [ this.#config.presentation.settings.os ].cursors;


            this._type = ( value in _cursors ) ? value : this._type;
        }

        /**
         * Get type property
         * @param           {string}                                    Type property
         */
        get type ( )
        {
            return this._type;
        }

    ////    [ POSITION ]    ////////////////////////////////

        /**
         * Set position property
         * @param           {Point} point                               X & Y coordinates
         */
        set position ( point )
        {
            this._position = ( Point.isPoint ( point ) ) ? point : this._position;


            if ( document.getElementById ( this.id ) != null )
            {
                let _cursor = document.getElementById ( this.id );

                    _cursor.style.left = point.x + 'px';

                    _cursor.style.top  = point.y + 'px';
            }
        }

        /**
         * Get position property
         * @return          {Point}                                     X & Y coordinates
         */
        get position ( )
        {
            return this._position;
        }

    ////    [ DISTANCE ]    ////////////////////////////////

        /**
         * Set distance property
         * @param           {string} id                                 Identifier of element
         */
        set distance ( id )
        {
            let _point = this.#tools.getCenterPoint ( document.getElementById ( id ) );


            this.#config.calculations.distance = Math.sqrt (

                                                    ( Math.pow ( _point.x - this.position.x, 2 ) ) +

                                                    ( Math.pow ( _point.y - this.position.y, 2 ) )

                                                 );
        }

        /**
         * Get distance property
         * @return          {number}                                    Distance from last distance check
         */
        get distance ( )
        {
            return this.#config.calculations.distance;
        }

    ////    [ ANGLE ]    ///////////////////////////////////

        /**
         * Set angle property
         * @param           {string} id                                 Identifier of element
         */
        set angle ( id )
        {
            let _point = this.#tools.getCenterPoint ( document.getElementById ( id ) );


            this.#config.calculations.angle = Math.atan2 (

                                                  _point.y - this.position.y,

                                                  _point.x - this.position.x

                                              );
        }

        /**
         * Get angle property
         * @return          {number}                                    Angle property; in radians
         */
        get angle ( )
        {
            return this.#config.calculations.angle;
        }

    ////    UTILITIES    ///////////////////////////////////

        /**
         * Checks whether the passed value is an instance of Cursor
         * @param           {Cursor}  value                             Cursor instance
         * @return          {boolean}                                   True | False
         */
        static isCursor ( value )
        {
            let _cursor = ( value instanceof Cursor );

            let _length = ( Object.keys ( value ).length == 2 );

            let _id     = ( value.hasOwnProperty ( 'id'   ) ) ? ( typeof value.id   === 'string' ) : false;

            let _type   = ( value.hasOwnProperty ( 'type' ) ) ? ( typeof value.type === 'string' ) : false;


            return ( _cursor || _length && _id && _type );
        }

        /**
         * Sets the next element
         * @param           {string} id                                 Identifier of element within DOM
         */
        nextElement ( id )
        {
            this.distance = id;

            this.angle    = id;


            this.#config.cache.position = this.#tools.getCenterPoint ( document.getElementById ( id ) );                  // @NOTE: cache this position for `toNextElement ( )` method; to ensure element's position is captured "pre-mouse event"
        }

        /**
         * Sends cursor to the location of the next element
         * @param           {string} id                                 Identifier of element within DOM
         */
        toNextElement ( id )
        {
            let _element = document.getElementById ( id );


            if ( _element != null ) this.position = this.#config.cache.position;
        }

        /**
         * Initiates any mouse actions associated with the passed 'element'
         * @param           {HTMLElement} element                       HTML DOM element
         */
        mouseAction ( element )
        {
            if ( mouseMove.sequence.constructor.name === 'Pattern' )
            {
                let _currentData = mouseMove.sequence.current;


                if ( _currentData.action === 'click' )

                    element.click ( );

                else

                    if ( element.getAttribute ( `on${_currentData.action}` ) != null )

                        eval ( `element.on${_currentData.action} ( );` );
            }
        }

        /**
         * Switch cursor's visual type
         * @param           {string} type                               Cursor type within #config.presentation [ os ].cursors [ type ]
         */
        switchType ( type = 'pointer' )
        {
            let _settings = this.#config.presentation.settings;

            let _delay    = 200;

            let _cursor   = document.getElementById ( this.id );


            this.type     = type;


            _settings.css.cursors [ 'top'  ] = this.#tools.pxToNumber ( _cursor.style.top  );

            _settings.css.cursors [ 'left' ] = this.#tools.pxToNumber ( _cursor.style.left );


            _cursor.src   = this.#config.presentation [ _settings.os ].cursors [ type ];

            _cursor.style = this.#tools.jsonToCss ( _settings.css.cursors );


            return new Promise ( ( resolve, reject ) => { setTimeout ( ( ) => { resolve ( ); }, _delay ); });
        }

        /**
         * Creates and mirrors a select input element; from the DOM
         * @param           {string} id                                 Identifier of element within DOM
         * @param           {string} id                                 Identifier of element within DOM
         */
        createSelectOptions ( id = { origin: undefined, target: undefined, option: undefined } )
        {
            /**
             * Creates a ul element, mirroring the passed select input element
             * @param           {Object} element                            Select element from DOM
             * @param           {Object} tools                              #tools object from 'this' context; this.#tools
             * @return          {Object}                                    Ul element with subsequent li elements
             */
            function _createUl ( element, tools )
            {
                let _checkMark        = '<img width="20" src="data:image/svg+xml;base64,"/>';


                let _ul               = document.createElement ( 'ul' );

                    _ul.style.cssText = tools.jsonToCss ( _css.ul );


                for ( let _i = 0; _i < element.length; _i++ )
                {
                    let _text = element [ _i ].innerText;


                    let _icon           = document.createElement ( 'span' );

                        _icon.style     = tools.jsonToCss ( _css.icon );

                        _icon.innerHTML = ( element [ _i ].attributes.selected != undefined ) ? _checkMark : ' ';


                    let _span           = document.createElement ( 'span' );

                        _span.innerText = _text;


                    let _li             = document.createElement ( 'li' );

                        _li.style       = tools.jsonToCss ( _css.li );

                        _li.id          = ( _option - 1 == _i ) ? id.target : '';

                        _li.setAttribute ( 'onmouseover', "this.style.background = 'rgb(73,133,218)';" );

                        _li.setAttribute ( 'onmouseout',  "this.style.background = 'none';" );

                        _li.setAttribute ( 'onclick',     `for ( let _child of this.parentElement.children ) _child.firstChild.firstChild.innerHTML = ' '; this.firstChild.firstChild.insertAdjacentHTML ( 'afterbegin', '${_checkMark}' ); for ( let _option of ${id.origin}.options ) if ( _option.attributes.selected != undefined ) _option.removeAttribute ( 'selected' ); else if ( _option.index == ${_option} - 1 ) _option.setAttribute ( 'selected', '' ); setTimeout ( ( ) => this.parentElement.style.display = 'none', 100 );` );

                    ////////////////////////////////////////////////////////////

                        _span.prepend   ( _icon );

                        _li.append      ( _span );

                        _ul.appendChild ( _li );
                }


                return _ul;
            }

            ////    OPTION    //////////////////////////////////////////////////

            let _option = id.option.match ( /(\d+)(?!.*\d)/ ) [ 0 ];

            ////    SELECT    //////////////////////////////////////////////////

            let _select = document.getElementById ( id.origin );

            let _point  = this.#tools.getCenterPoint ( _select );

            ////    CSS    /////////////////////////////////////////////////////

            let _css         = this.#config.presentation.settings.css.ui.select;

                _css.ul.left = _point.x - ( _select.getBoundingClientRect ( ).width  / 2 );

                _css.ul.top  = _point.y - ( _select.getBoundingClientRect ( ).height / 2 );

            ////    UL CREATION    /////////////////////////////////////////////

            document.body.appendChild ( _createUl ( _select, this.#tools ) );
        }

        /**
         * Set's this cursor's mouse interaction type; with other DOM elements
         */
        setInteraction ( )
        {
            let _elements = document.elementsFromPoint ( this.position.x, this.position.y );

            let _avoid    = this.#config.cache.avoid;

            let _wasOver  = this.#config.cache.over;


            for ( let _element of _elements )

                if ( ! _avoid.includes ( _element.id ) )

                    if ( ! _wasOver.includes ( _element ) )
                    {
                        this.#tools.setCursorType ( _element );

                        this.#tools.mouseover     ( _element );
                    }

                else

                    if ( _wasOver.length > 1 )

                        for ( let _over of _wasOver )

                            this.#tools.mouseout ( _over );
        }
}
